<#
.SYNOPSIS

This command do the git pull on given $location

.DESCRIPTION

executes git pull

.PARAMETER location

git repository path

.OUTPUTS

No return
#>

function Invoke-GitPull{	
	& "$PSScriptRoot\commandLine\git-action.bat" pull 	
}

function Invoke-GitPush{
	& "$PSScriptRoot\commandLine\git-action.bat" push
}

function Invoke-GitCommit{
	[CmdletBinding()]
	Param(		
		[string] $Message
	)	
	& "$PSScriptRoot\commandLine\git-action.bat" commit $Message
}

function Invoke-GitAdd{

	[CmdletBinding()]
	Param(		
		[string] $Path
	)	
	
	& "$PSScriptRoot\commandLine\git-action.bat" add $path
}

function Invoke-GitBranch{

	[CmdletBinding()]
	Param(		
		[string] $Name
	)	
	
	& "$PSScriptRoot\commandLine\git-action.bat" branch $Name
}

function Invoke-GitCheckout{

	[CmdletBinding()]
	Param(		
		[string] $Name
	)	
	
	& "$PSScriptRoot\commandLine\git-action.bat" checkout $Name
}



